SELECT order_id, order_code, branch_office_id, order_name, total_amount, purchase_vat, order_date, register_date, due_date, order_status, employee_num, company_num FROM (SELECT ROW_NUMBER() OVER ( ORDER BY TO_NUMBER(REGEXP_SUBSTR(order_id, '\d+')) desc ) AS RN, order_id, order_code, branch_office_id, order_name, total_amount, purchase_vat, order_date, register_date, due_date, order_status, employee_num, company_num FROM bsn_order WHERE company_num = 1
AND ROWNUM <= #{end}
) WHERE RN >= #{start}
