SELECT ORDER_CODE ,branch_office_id ,order_date ,due_date ,remark ,company_num FROM ( SELECT /*+ INDEX_ASC(BHF_ORDER PK_BHF_ORDER) */ ROWNUM RN ,ORDER_CODE ,branch_office_id ,order_date ,due_date ,remark ,company_num FROM BHF_ORDER WHERE progress_status = '신청' -- AND company_num = 1
AND ROWNUM <= #{end}
) WHERE RN >= #{start}
ORDER BY TO_NUMBER(REGEXP_SUBSTR(order_code, '\d+')), order_date, due_date

SELECT order_detail_code, order_code, goods_code, goods_name, option_code, option_name, goods_standard, quantity FROM ( SELECT /*+ INDEX_ASC(bhf_order_detail order_detail_code) */ ROWNUM RN ,order_detail_code, order_code, goods_code, goods_name, option_code, option_name, goods_standard, quantity FROM bhf_order_detail WHERE order_code = #{orderCode}
AND ROWNUM <= #{end}
) WHERE RN >= #{start}
ORDER BY order_detail_code
