mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RN FROM ( SELECT c.COMPANY_NUM, c.COMPANY_NAME, c.COMPANY_ENG_NAME, c.REPRESENTATION_NAME, c.REPRESENTATION_PHONE, c.CHARGER_NAME, c.CHARGER_PHONE, c.CHARGER_EMAIL, c.COMPANY_ADDRESS, c.BUSINESS_NUM, c.BUSINESS_LICENSE, c.SERVICE_STATE, c.REGISTER_DATE, d.contract_num, l.subscription_num, l.subscription_end_date, l.subscription_form from erp_company c left join erp_contract_document d on c.company_num = d.company_num left join erp_subscription_info_list l on c.company_num = l.company_num and l.subscription_name_num in (1, 6) order by c.COMPANY_NUM DESC ) A
WHERE ROWNUM <= #{end}
)
WHERE RN >= #{start}
