mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rn, data.* FROM ( SELECT C.CONTRACT_NUM, E.EMPLOYEE_NUM, E.EMPLOYEE_NAME, D.DEPARTMENT_NAME AS departmentName, PARENT_D.DEPARTMENT_NAME AS parentDepartmentName, C.CONTRACT_TYPE, TO_CHAR(C.CONTRACT_START_DATE, 'YYYY-MM-DD') AS CONTRACT_START_DATE, TO_CHAR(C.CONTRACT_END_DATE, 'YYYY-MM-DD') AS CONTRACT_END_DATE, C.MONTHLY_SALARY, C.CONTRACT_STATUS FROM HR_EMPLOYEE_CONTRACT C JOIN HR_EMPLOYEE E ON C.EMPLOYEE_NUM = E.EMPLOYEE_NUM JOIN HR_DEPARTMENT D ON C.DEPARTMENT_NUM = D.DEPARTMENT_NUM LEFT JOIN HR_DEPARTMENT PARENT_D ON D.PARENT_DEPARTMENT_NUM = PARENT_D.DEPARTMENT_NUM WHERE C.COMPANY_NUM = #{companyNum} ORDER BY C.CONTRACT_START_DATE DESC ) data
WHERE ROWNUM <= #{end} )
WHERE rn >= #{start}
