mapper.select:
SELECT * FROM ( SELECT ROWNUM rn, employeeNum, employeeId, employeeName, email, phone, hireDate, position, status, employmentType, departmentName FROM ( SELECT e.employee_num AS employeeNum, e.employee_id AS employeeId, e.employee_name AS employeeName, e.email AS email, e.phone AS phone, e.hire_date AS hireDate, fn_get_cmmn_name(e.position) AS position, fn_get_cmmn_name(e.status) AS status, fn_get_cmmn_name(e.employment_type) AS employmentType, d.department_name AS departmentName FROM hr_employee e LEFT JOIN hr_department d ON e.department_num = d.department_num
AND d.department_name = #{department}
AND e.position = #{position}
AND e.status = #{status}
AND e.employment_type = #{employmentType}
AND e.employee_name LIKE '%' || #{searchKeyword} || '%'
AND e.employee_id LIKE '%' || #{searchKeyword} || '%'
AND e.phone LIKE '%' || #{searchKeyword} || '%'
)
WHERE ROWNUM <= #{end}
) WHERE rn >= #{start}
