mapper.select:
SELECT employee_num, employee_id, employee_pw, employee_name, email, phone, position, company_num, department_num, authority, company_eng_name FROM ( -- hr_employee에서 가져오기 SELECT h.employee_num, h.employee_id, h.employee_pw, h.employee_name, h.email, h.phone, h.position, h.company_num, h.department_num, h.authority, e.company_eng_name FROM hr_employee h JOIN erp_company e ON h.company_num = e.company_num WHERE h.employee_id = #{employeeId}
UNION ALL
-- erp_user_accounts에서 가져오기 SELECT NULL AS employee_num, -- NULL이면 0으로 변환 u.employee_id, u.employee_pw, NULL AS employee_name, NULL AS email, NULL AS phone, NULL AS position, u.company_num, NULL AS department_num, u.authority, NULL AS company_eng_name FROM erp_user_accounts u JOIN erp_company e ON u.company_num = e.company_num WHERE u.employee_id = #{employeeId} ) all_accounts

SELECT 'ROLE_' || c.cmmn_code AS role_name FROM hr_employee h JOIN cmmn c ON h.authority = c.cmmn_code WHERE h.employee_id = #{employeeId}
UNION ALL
SELECT 'ROLE_' || c.cmmn_code AS role_name FROM erp_user_accounts u JOIN cmmn c ON u.authority = c.cmmn_code WHERE u.employee_id = #{employeeId}
